local assets = {     Asset("ANIM", "anim/swap_riko_sack.zip"),     Asset("ANIM", "anim/ui_krampusbag_2x5.zip"),      Asset("IMAGE", "images/inventoryimages/riko_sack.tex"),     Asset("ATLAS", "images/inventoryimages/riko_sack.xml"), }  local containers = require("containers")  local oldwidgetsetup = containers.widgetsetup containers.widgetsetup = function(container, prefab)     if not prefab and container.inst.prefab == "riko_sack" then         prefab = "krampus_sack"     end     oldwidgetsetup(container, prefab) end  local function onequip(inst, owner)     owner.AnimState:OverrideSymbol("swap_body", "swap_riko_sack", "backpack")     owner.AnimState:OverrideSymbol("swap_body", "swap_riko_sack", "swap_body")     inst.components.container:Open(owner) end  local function onunequip(inst, owner)     owner.AnimState:ClearOverrideSymbol("swap_body")     owner.AnimState:ClearOverrideSymbol("backpack")     inst.components.container:Close(owner) end  local function fn()     local inst = CreateEntity()      inst.entity:AddTransform()     inst.entity:AddAnimState()     inst.entity:AddMiniMapEntity()     inst.entity:AddNetwork()      MakeInventoryPhysics(inst)      inst.MiniMapEntity:SetIcon("riko_sack.png")      inst.AnimState:SetBank("rikosack")     inst.AnimState:SetBuild("swap_riko_sack")     inst.AnimState:PlayAnimation("anim")      inst.foleysound = "dontstarve/movement/foley/krampuspack"      inst:AddTag("backpack")      inst:AddTag("waterproofer")      inst.entity:SetPristine()      if not TheWorld.ismastersim then         return inst     end      inst:AddComponent("inspectable")      inst:AddComponent("inventoryitem")     inst.components.inventoryitem.atlasname = "images/inventoryimages/riko_sack.xml"     inst.components.inventoryitem.cangoincontainer = false      inst:AddComponent("equippable")     inst.components.equippable.equipslot = EQUIPSLOTS.BACK or EQUIPSLOTS.BODY     inst.components.equippable:SetOnEquip(onequip)     inst.components.equippable:SetOnUnequip(onunequip)     inst.components.equippable.walkspeedmult = TUNING.PIGGYBACK_SPEED_MULT      inst:AddComponent("waterproofer")     inst.components.waterproofer:SetEffectiveness(0)      inst:AddComponent("container")     inst.components.container:WidgetSetup("krampus_sack")      MakeHauntableLaunchAndDropFirstItem(inst)      return inst end  STRINGS.NAMES.RIKO_SACK= "探窟背包" STRINGS.CHARACTERS.GENERIC.DESCRIBE.RIKO_SACK= "好沉啊。。"  return Prefab("riko_sack", fn, assets) 