local function MakeFood(name, type, health, hunger, sanity, perishtime) local assets = { Asset("ANIM", "anim/"..name..".zip"),     Asset("ATLAS", "images/inventoryimages/"..name..".xml"), }  local prefabs =  { "spoiled_food", }  local function fn(Sim) local inst = CreateEntity() inst.entity:AddTransform() inst.entity:AddAnimState() if TheSim:GetGameID() =="DST" then inst.entity:AddNetwork() end  MakeInventoryPhysics(inst) MakeSmallBurnable(inst) MakeSmallPropagator(inst)  inst.AnimState:SetBank(name) inst.AnimState:SetBuild(name) inst.AnimState:PlayAnimation("idle")  inst:AddTag("preparedfood") inst:AddTag("rikofood")      if TheSim:GetGameID()=="DST" then if not TheWorld.ismastersim then return inst end  inst.entity:SetPristine() end  inst:AddComponent("edible") inst.components.edible.foodtype = type inst.components.edible.healthvalue = health inst.components.edible.hungervalue = hunger inst.components.edible.sanityvalue = sanity  inst:AddComponent("inspectable") inst:AddComponent("tradable")  inst:AddComponent("inventoryitem") inst.components.inventoryitem.atlasname = "images/inventoryimages/"..name..".xml"  inst:AddComponent("stackable") inst.components.stackable.maxsize = TUNING.STACK_SIZE_SMALLITEM  if perishtime ~= nil then inst:AddComponent("perishable") inst.components.perishable:SetPerishTime(perishtime) inst.components.perishable:StartPerishing() inst.components.perishable.onperishreplacement = "spoiled_food" end  return inst end  return Prefab(name, fn, assets, prefabs) end  STRINGS.NAMES.RIKO_ONIGIRI= "莉可爆弹" STRINGS.CHARACTERS.GENERIC.DESCRIBE.RIKO_ONIGIRI= "和名字一样的饭团。" STRINGS.NAMES.RIKO_DASHI= "莉可汤" STRINGS.CHARACTERS.GENERIC.DESCRIBE.RIKO_DASHI= "味道超丰富！" STRINGS.NAMES.RIKO_GRILL= "莉可烤肉" STRINGS.CHARACTERS.GENERIC.DESCRIBE.RIKO_GRILL= "触动本能的美味……！" STRINGS.NAMES.RIKO_FRIEDFISH= "煎炸岩辉鳟" STRINGS.CHARACTERS.GENERIC.DESCRIBE.RIKO_FRIEDFISH= "停不下来……" STRINGS.NAMES.RIKO_SNACK= "莉可小碟" STRINGS.CHARACTERS.GENERIC.DESCRIBE.RIKO_SNACK= "孤儿院的节日料理。"  return MakeFood("riko_onigiri",FOODTYPE.VEGGIE, 12, 62.5, 5, TUNING.PERISH_MED), MakeFood("riko_dashi", FOODTYPE.MEAT, 60, 37.5, 33, TUNING.PERISH_MED), MakeFood("riko_grill", FOODTYPE.MEAT, 3, 37.5, 5, TUNING.PERISH_MED), MakeFood("riko_friedfish", FOODTYPE.MEAT, 80, 50, 15, nil), MakeFood("riko_snack", FOODTYPE.VEGGIE, 10, 12.5, 50, TUNING.PERISH_MED)